const noData = {
  tokenId: '',
  amount: ''
};

// TODO: add tests
export function getTokenFromData(data?: string): {
  tokenId: string;
  amount: string;
} {
  const tokenTransfer = data?.includes(TransactionTypesEnum.ESDTTransfer);
  const nftTransfer = data?.includes(TransactionTypesEnum.ESDTNFTTransfer);

  if (data != null && (tokenTransfer || nftTransfer)) {
    try {
      const encodedToken = data.split('@')[1];
      const encodedAmount = data.split('@')[tokenTransfer ? 2 : 3];
      const tokenId = Buffer.from(encodedToken, 'hex').toString('ascii');

      if (tokenId) {
        const amount = new BigNumber(
          '0x' + encodedAmount.replace('0x', '')
        ).toString(10);
        return {
          tokenId,
          amount
        };
      } else {
        return noData;
      }
    } catch (e) {
      return noData;
    }
  }
  return noData;
}
export default getTokenFromData;
export function isTokenTransfer({
  tokenId,
  erdLabel
}: {
  tokenId: string | undefined;
  erdLabel: string;
}) {
  return Boolean(tokenId && tokenId !== erdLabel);
}

export default isTokenTransfer;
function buildUrlParams(
  search: string,
  urlParams: {
    [key: string]: string;
  }
) {
  const urlSearchParams = new URLSearchParams(search);
  const params = Object.fromEntries(urlSearchParams as any);

  const nextUrlParams = new URLSearchParams({
    ...params,
    ...urlParams
  }).toString();

  return { nextUrlParams, params };
}

interface ReplyUrlType {
  callbackUrl: string;
  urlParams?: { [key: string]: string };
}

export function buildReplyUrl({ callbackUrl, urlParams = {} }: ReplyUrlType) {
  let url = callbackUrl;
  if (Object.entries(urlParams).length > 0) {
    const { search, origin, pathname } = new URL(callbackUrl);
    const { nextUrlParams } = buildUrlParams(search, urlParams);
    url = `${origin}${pathname}?${nextUrlParams}`;
  }
  return url;
}

export default buildReplyUrl;
import {
  Transaction,
  TransactionOptions,
  TransactionVersion
} from '@elrondnetwork/erdjs';
import { TransactionServerStatusesEnum } from 'types/enums';

export function parseTransactionAfterSigning(
  transaction: Transaction,
  isLedger = false
) {
  // TODO: REMOVE
  //#region REMOVE when options is available in erdjs getTransactionsFromWalletUrl
  if (isLedger) {
    transaction.version = TransactionVersion.withTxHashSignVersion();
    transaction.options = TransactionOptions.withTxHashSignOptions();
  }
  //#endregion
  const parsedTransaction = transaction.toPlainObject();
  parsedTransaction.hash = transaction.getHash().toString();
  parsedTransaction.status = TransactionServerStatusesEnum.pending;
  return parsedTransaction;
}

