interface SwitchTrueType {
  default: any;
  [key: string]: any;
}

type ReturnKeys = 'true' | 'false';

export function switchTrue(object: SwitchTrueType) {
  const { default: defaultValue, ...rest } = object;
  const obj = { default: defaultValue, ...rest };
  const result = Object.keys(obj).reduce((acc, cur) => {
    return {
      ...acc,
      [cur === 'default' ? 'true' : cur]: (obj as any)[cur as ReturnKeys]
    };
  }, {} as Record<ReturnKeys, any>);
  return result['true'];
}
import { Address } from '@elrondnetwork/erdjs';

function canTransformToPublicKey(address: string) {
  try {
    const checkAddress = new Address(address);
    return Boolean(checkAddress.bech32());
  } catch {
    return false;
  }
}

export function addressIsValid(destinationAddress: string) {
  const isValidBach =
    destinationAddress?.startsWith('erd') &&
    destinationAddress.length === 62 &&
    /^\w+$/.test(destinationAddress);

  return isValidBach && canTransformToPublicKey(destinationAddress);
}
