import * as React from 'react';
import { useGetAccountInfo, DappUI } from '@elrondnetwork/dapp-core';
import { contractAddress } from 'config';

const TopInfo = () => {
  const { address, account } = useGetAccountInfo();
  let apiElrond = 'https://api.elrond.com/accounts/';
  //const usernameA = () =>
  //{ href: '{https://api.elrond.com/accounts/}' };
  //href={`${network.explorerAddress}/transactions/${tx.txHash}`}

  return (
    <div className='text-white' data-testid='topInfo'>
      <div className='mb-1'>
        <span className='opacity-6 mr-1'>Your address:</span>
        <span data-testid='accountAddress'> {address}</span>
        <p className='text-center'>Account Name: </p>
        <span className='text-danger'>
          {apiElrond}
          {account.username}
        </span>
      </div>
      <div className='mb-4'>
        <span className='opacity-6 mr-1'>Contract address:</span>
        <span data-testid='contractAddress'> {contractAddress}</span>
      </div>
      <div>
        <h3 className='py-2'>
          <DappUI.Denominate value={account.balance} data-testid='balance' />
        </h3>
      </div>
    </div>
  );
};

export default TopInfo;
