import * as React from 'react';
import {
  useGetAccountInfo,
  DappUI,
  useGetNetworkConfig
} from '@elrondnetwork/dapp-core';
import { contractAddress } from 'config';

const RegisterInfo = () => {
  const { address, account } = useGetAccountInfo();
  const { network } = useGetNetworkConfig();

  const isRegistered = Boolean(address);

  return (
    <div className='container-fluid p-1'>
      {isRegistered && (
        <div className='text-white bg-info p-4 m-2 my-3 rounded border-1'>
          <div className='bg-light m-0 p-0'>
            <h1>Register to Vote</h1>
            <div className='mb-2'>
              <span className='opacity-7 mr-1'>
                Your address:&nbsp;&nbsp;
                <span data-testid='accountAddress'>
                  <a
                    className='text-white'
                    href={`${network.explorerAddress}/address/${address}`}
                    {...{
                      target: '_blank'
                    }}
                    rel='noopener noreferrer'
                    title='View in Explorer'
                  >
                    <DappUI.Trim data-testid='accountAddress' text={address} />
                  </a>
                </span>
              </span>
            </div>
            <div className='mb-2'>
              <span className='opacity-6 mr-1'>Contract address:</span>
              <span data-testid='contractAddress'> {contractAddress}</span>
            </div>
            <div>
              <h3 className='py-2'>
                <DappUI.Denominate
                  value={account.balance}
                  data-testid='balance'
                />
              </h3>
            </div>
          </div>
        </div>
      )}
      {!isRegistered && (
        <div className='text-white bg-dark p-4 m-2 my-3 rounded border-1'>
          <h1>Already Registered</h1>
          <div className='mb-2'>
            <span className='opacity-6 mr-1'>Your address:</span>
            <span data-testid='accountAddress'> {address}</span>
          </div>
          <div className='mb-2'>
            <span className='opacity-6 mr-1'>Contract address:</span>
            <span data-testid='contractAddress'> {contractAddress}</span>
          </div>
          <div>
            <h3 className='py-2'>
              <DappUI.Denominate
                value={account.balance}
                data-testid='balance'
              />
            </h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegisterInfo;
