import * as React from 'react';
import { useGetAccountInfo, DappUI } from '@elrondnetwork/dapp-core';
import { contractAddress } from 'config';
import Actions from './Actions';
import TopInfo from './TopInfo';

const StatutsVote = () => {
  const { address, account } = useGetAccountInfo();

  const isRegistered = Boolean(address);

  return (
    <div className='container-fluid p-1'>
      {isRegistered && (
        <div className='card-deck p-2'>
          <div className='card rounded border-1 mb-4 bg-primary'>
            <div className='card-body text-center p-4'>
              <TopInfo />
              <Actions />
            </div>
          </div>
          <div className='card rounded border-1 mb-4 bg-secondary'>
            <div className='card-body text-center p-4'>
              <TopInfo />
              <Actions />
            </div>
          </div>
        </div>
      )}
      {!isRegistered && (
        <div
          className='text-white bg-dark p-4 m-2 my-3 rounded border-1'
          data-testid='topInfo'
        >
          <h1>Register to Vote</h1>
          <div className='mb-2'>
            <span className='opacity-6 mr-1'>Your address:</span>
            <span data-testid='accountAddress'> {address}</span>
          </div>
          <div className='mb-2'>
            <span className='opacity-6 mr-1'>Contract address:</span>
            <span data-testid='contractAddress'> {contractAddress}</span>
          </div>
          <div>
            <h3 className='py-2'>
              <DappUI.Denominate
                value={account.balance}
                data-testid='balance'
              />
            </h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default StatutsVote;
