import * as React from 'react';
import { useGetAccountInfo, DappUI } from '@elrondnetwork/dapp-core';
import { contractAddress } from 'config';
import Actions from './Actions';
import TopInfo from './TopInfo';

const StatutsVote = () => {
  const { address, account } = useGetAccountInfo();

  const isRegistered = Boolean(address);

  return (
    <div className='container-fluid p-1'>
      {isRegistered && (
        <div className='card-deck p-1'>
          <div className='card p-2 pt-4 mt-2 bg-warning border-1'>
            <div className='card bg-light border-primary border-1 p-2'>
              <h3 className='text-center my-2 text-primary'>
                Conservative Party Votes
              </h3>
              <div className='card d-flex justify-content-center px-2 pt-2 m-3 bg-secondary border-1'>
                <h3 className='text-center'>45544444</h3>
              </div>
              <div className='card p-3'>
                <img
                  className='card-img-top img-fluid bg-primary p-1'
                  src='../images/Boris-Johnson-profile.jpg'
                ></img>
              </div>
            </div>
            <div className='card p-2 my-2 mx-2'>
              <Actions />
            </div>
          </div>
          <div className='card p-4 bg-light border-1'>
            <div className='card-body text-center p-4'>
              <Actions />
            </div>
          </div>
        </div>
      )}
      {!isRegistered && (
        <div className='card-deck p-2'>
          <div className='card rounded border-1 mb-4 bg-secondary'>
            <div className='card-body text-center p-4'>
              <TopInfo />
              <Actions />
            </div>
          </div>
          <div className='card rounded border-1 mb-4 bg-secondary'>
            <div className='card-body text-center p-4'>
              <TopInfo />
              <Actions />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StatutsVote;
