import * as React from 'react';
import {
  useGetAccountInfo,
  DappUI,
  useGetNetworkConfig,
  transactionServices,
  refreshAccount
} from '@elrondnetwork/dapp-core';
import { contractAddress } from 'config';

const RegisterInfo = () => {
  const { address, account } = useGetAccountInfo();
  const { network } = useGetNetworkConfig();

  const isRegistered = Boolean(address);

  const /*transactionSessionId*/ [, setTransactionSessionId] = React.useState<
      string | null
    >(null);

  const { sendTransactions } = transactionServices;

  const sendPingTransaction = async () => {
    const pingTransaction = {
      value: '1000000000000000000',
      data: 'ping',
      receiver: contractAddress
    };
    await refreshAccount();

    const { sessionId /*, error*/ } = await sendTransactions({
      transactions: pingTransaction,
      transactionsDisplayInfo: {
        processingMessage: 'Processing Ping transaction',
        errorMessage: 'An error has occured during Ping',
        successMessage: 'Ping transaction successful'
      },
      redirectAfterSign: false
    });
    if (sessionId != null) {
      setTransactionSessionId(sessionId);
    }
  };

  function registerID() {
    const input = document.getElementById('inputID');
    alert(input);
  }

  return (
    <div className='container-fluid p-1'>
      {isRegistered && (
        <div className='text-dark bg-light p-4 m-2 my-3 border rounded border-info border-1'>
          <div className='card-deck p-2'>
            <h1 className='mb-4'>Register to Vote</h1>
            <form className='form-inline'>
              <div className='form-group mx-1 mb-2'>
                <input
                  type='text'
                  autoComplete='off'
                  maxLength={9}
                  minLength={9}
                  className='form-control'
                  id='inputID'
                  placeholder='National ID'
                ></input>
                <button
                  className='btn btn-outline-success mx-sm-3 '
                  type='submit'
                  onClick={registerID}
                >
                  Register
                </button>
              </div>
            </form>
          </div>
          <div className='mb-2'>
            <span className='opacity-7 mr-1'>
              Your address:&nbsp;
              <span data-testid='accountAddress'>
                <a
                  className='text-dark'
                  href={`${network.explorerAddress}/address/${address}`}
                  {...{
                    target: '_blank'
                  }}
                  rel='noopener noreferrer'
                  title='View in Explorer'
                >
                  <DappUI.Trim data-testid='accountAddress' text={address} />
                </a>
              </span>
            </span>
          </div>
        </div>
      )}
      {!isRegistered && (
        <div className='text-white bg-dark p-4 m-2 my-3 rounded border-1'>
          <h1>Already Registered</h1>
          <div className='mb-2'>
            <span className='opacity-6 mr-1'>Your address:</span>
            <span data-testid='accountAddress'> {address}</span>
          </div>
          <div className='mb-2'>
            <span className='opacity-6 mr-1'>Contract address:</span>
            <span data-testid='contractAddress'> {contractAddress}</span>
          </div>
          <div>
            <h3 className='py-2'>
              <DappUI.Denominate
                value={account.balance}
                data-testid='balance'
              />
            </h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegisterInfo;
