import * as React from 'react';
import { Link } from 'react-router-dom';
import { dAppName } from 'config';
import { routeNames } from 'routes';

const Home = () => {
  return (
    <div className='d-flex flex-fill container'>
      <div className='row w-100 py-4'>
        <div className='col-12 col-md-10 col-lg-12 mx-auto'>
          <div className='card shadow border-info bg-light rounded p-1 border-2'>
            <div className='card-body text-center'>
              <h2 className='mb-3 h1' data-testid='title'>
                {dAppName}
              </h2>

              <p className='mb-2'>
                An descentralised online voting system developed on the Elrond
                Network.
              </p>
              <h2 className='text-primary'>Transparent - Secure - Immutable</h2>
            </div>
          </div>
        </div>
        <div className='card-bottom row w-20'>
          <div className='card col-12 col-md-10 col-lg-12 mx-auto'>
            <Link
              to={routeNames.unlock}
              className='btn btn-primary mt-3 text-white'
              data-testid='loginBtn'
            >
              Login
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
