import React from 'react';
import {
  DappUI,
  //getIsLoggedIn,
  logout,
  useGetAccountInfo,
  useGetNetworkConfig
} from '@elrondnetwork/dapp-core';
import { Navbar as BsNavbar, NavItem, Nav } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import { contractAddress1, dAppName } from 'config';
import { routeNames } from 'routes';
import { ReactComponent as ElrondLogo } from './../../../assets/img/elrond.svg';

const Navbar = () => {
  const { address } = useGetAccountInfo();
  const { network } = useGetNetworkConfig();
  const handleLogout = () => {
    logout(`${window.location.origin}${routeNames.home}`);
  };

  const isLoggedIn = Boolean(address);

  const { search } = useLocation();
  const isCheckPage = Boolean(search);

  return (
    <BsNavbar className='bg-light border-bottom border-info px-5 py-3'>
      <div className='container-fluid'>
        <Link
          className='d-flex align-items-center navbar-brand mr-0'
          to={isLoggedIn ? routeNames.dashboard : routeNames.home}
        >
          <ElrondLogo className='elrond-logo' />
          <span className='dapp-name text-info'>{dAppName}</span>
        </Link>

        <Nav className='ml-auto'>
          {isLoggedIn && (
            <NavItem>
              <div className='card-deck'>
                <div className='card w-100 mx-0 '>
                  <a
                    href={`${network.explorerAddress}/address/${contractAddress1}`}
                    {...{
                      target: '_blank'
                    }}
                    title='View in Explorer'
                  >
                    <DappUI.Trim
                      data-testid='contractAddress1'
                      text={contractAddress1}
                    />
                  </a>
                </div>
                <div className='card ml-0 '>
                  <button
                    className='btn btn-outline-info btn-lg'
                    onClick={handleLogout}
                  >
                    Close
                  </button>
                </div>
              </div>
            </NavItem>
          )}
          {!isLoggedIn && !isCheckPage && (
            <NavItem>
              <Link
                to={routeNames.unlock}
                className='btn btn-outline-primary bg-primary btn-lg m-3 text-light'
                data-testid='loginBtn'
              >
                Login
              </Link>
            </NavItem>
          )}
          {!isLoggedIn && !isCheckPage && (
            <NavItem>
              <Link
                to={routeNames.home}
                className='btn btn-outline-primary btn-lg m-3'
                data-testid='loginBtn'
              >
                Back
              </Link>
            </NavItem>
          )}
        </Nav>
      </div>
    </BsNavbar>
  );
};

export default Navbar;
