import * as React from 'react';
import {
  transactionServices,
  useGetAccountInfo,
  useGetPendingTransactions,
  refreshAccount,
  useGetNetworkConfig
} from '@elrondnetwork/dapp-core';
import {
  Address,
  AddressValue,
  ContractFunction,
  ProxyProvider,
  Query
} from '@elrondnetwork/erdjs';
//import { faArrowUp, faArrowDown } from '@fortawesome/free-solid-svg-icons';
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
//import moment from 'moment';
import { contractAddress } from 'config';

const ActionsRed = () => {
  const account = useGetAccountInfo();
  const { hasPendingTransactions } = useGetPendingTransactions();
  const { network } = useGetNetworkConfig();
  const { address } = account;

  const [secondsLeft, setSecondsLeft] = React.useState<number>();
  const [hasPing, setHasPing] = React.useState(false);
  const /*transactionSessionId*/ [, setTransactionSessionId] = React.useState<
      string | null
    >(null);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  //React.useEffect(mount, [hasPing]);

  React.useEffect(() => {
    const query = new Query({
      address: new Address(contractAddress),
      func: new ContractFunction('didUserPing'),
      args: [new AddressValue(new Address(address))]
    });
    const proxy = new ProxyProvider(network.apiAddress);
    proxy.queryContract(query).then(({ isSuccess }) => {
      if (isSuccess()) {
        setHasPing(true);
      } else {
        setHasPing(false);
      }
    });

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [hasPendingTransactions]);

  const { sendTransactions } = transactionServices;

  const sendPingTransaction = async () => {
    const pingTransaction = {
      value: '1000000000000000000',
      data: 'ping',
      receiver: contractAddress
    };
    await refreshAccount();

    const { sessionId /*, error*/ } = await sendTransactions({
      transactions: pingTransaction,
      transactionsDisplayInfo: {
        processingMessage: 'Processing Ping transaction',
        errorMessage: 'An error has occured during Ping',
        successMessage: 'Ping transaction successful'
      },
      redirectAfterSign: false
    });
    if (sessionId != null) {
      setTransactionSessionId(sessionId);
    }
  };
  const pongAllowed = secondsLeft === 0 && !hasPendingTransactions;
  const notAllowedClass = pongAllowed ? '' : 'not-allowed disabled';

  return (
    <div className='d-flex col justify-content-center'>
      {hasPing && !hasPendingTransactions && (
        <div
          className='card row w-100 border-danger shadow mx-auto'
          onClick={sendPingTransaction}
        >
          <button type='button' className='btn btn-danger m-2'>
            Vote
          </button>
          <a href='/' className='text-dark'></a>
        </div>
      )}
      {!hasPing && !hasPendingTransactions && (
        <div className='d-flex col justify-content-center not-allowed disable'>
          <div className='card row w-100 border-danger shadow mx-auto not-allowed disable'>
            <button
              type='button'
              className='btn btn-danger m-2 not-allowed disabled'
            >
              Voted
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ActionsRed;
