import React from 'react';
import {
  //getIsLoggedIn,
  logout,
  useGetAccountInfo
} from '@elrondnetwork/dapp-core';
import { Navbar as BsNavbar, NavItem, Nav } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import { dAppName } from 'config';
import { routeNames } from 'routes';
import { ReactComponent as ElrondLogo } from './../../../assets/img/elrond.svg';

const Navbar = () => {
  const { address } = useGetAccountInfo();

  const handleLogout = () => {
    logout(`${window.location.origin}${routeNames.home}`);
  };

  const isLoggedIn = Boolean(address);

  const { search } = useLocation();
  const isCheckPage = Boolean(search);

  return (
    <BsNavbar className='bg-light border-bottom border-info px-5 py-3'>
      <div className='container-fluid'>
        <Link
          className='d-flex align-items-center navbar-brand mr-0'
          to={isLoggedIn ? routeNames.dashboard : routeNames.home}
        >
          <ElrondLogo className='elrond-logo' />
          <span className='dapp-name text-info'>{dAppName}</span>
        </Link>

        <Nav className='ml-auto'>
          {isLoggedIn && (
            <NavItem>
              <button
                className='btn btn-outline-info btn-lg'
                onClick={handleLogout}
              >
                Close
              </button>
            </NavItem>
          )}
          {(!isLoggedIn && !isCheckPage) && (
            <NavItem>
              <Link
                to={routeNames.unlock}
                className='btn btn-primary m-3 text-white'
                data-testid='loginBtn'
              >
                {isCheckPage}Login
              </Link>
            </NavItem>
          )}
          {(!isLoggedIn && isCheckPage) && (
            <NavItem>
              <Link
                to={routeNames.unlock}
                className='btn btn-primary m-3 text-white'
                data-testid='loginBtn'
              >
                {isCheckPage}Login
              </Link>
            </NavItem>
          )}
        </Nav>
      </div>
    </BsNavbar>
  );
};

export default Navbar;
