import { fs } from 'fs';
import * as React from 'react';
import { useGetAccountInfo, DappUI } from '@elrondnetwork/dapp-core';
import { core, ElrondProxy } from '@elrondnetwork/elrond-core-js';
import { sendWallet } from 'config';
import './myWallet.json';
import './passMyWallet';
import { ProxyProvider } from '@elrondnetwork/erdjs/out';

const SendToken = () => {
  let keyFileJson = fs
    .readFileSync('myWallet.json', { encoding: 'utf8' })
    .trim();
  let keyFileObject = JSON.parse(keyFileJson);

  let accountSend = new core.accountSend();
  accountSend.loadFromKeyFile(keyFileObject, 'passMyWallet');

  let transaction = new core.transaction(
    12,
    'erd16wwuy5kk46auwuawurempkkvh0kjrkys30fjjlxp5ht6nj4uxl2sknxpmg',
    'erd16wwuy5kk46auwuawurempkkvh0kjrkys30fjjlxp5ht6nj4uxl2sknxpmg',
    '10000000000000000',
    1000000000,
    70000,
    'ESDTTransfer',
    'D',
    1
  );

  let serializedTransaction = transaction.prepareForSigning();
  transaction.signature = accountSend.sign(serializedTransaction);
  let signedTransaction = transaction.prepareForNode();
  let signedTransactionJson = JSON.stringify(signedTransaction, null, 4);
  signedTransactionJson(ElrondProxy);
};

export default SendToken;
